import &#123; Directive, HostListener, OnInit &#125; from '@angular/core';import &#123; MatDialogContainer, MatDialogRef &#125; from '@angular/material';import &#123; Subscription, fromEvent &#125; from 'rxjs';import &#123; takeUntil &#125; from 'rxjs/operators';export interface Position &#123;  x: number;  y: number;&#125;@Directive(&#123;  selector: '[dialog-draggable]'&#125;)export class DialogDraggableDirective implements OnInit &#123;  private _subscription: Subscription;  mouseStart: Position;  mouseDelta: Position;  offset: Position;  constructor(    private matDialogRef: MatDialogRef&lt;any&gt;,    private container: MatDialogContainer  ) &#123;  &#125;  ngOnInit() &#123;    this.offset = this._getOffset();    this._updatePosition(this.offset.y, this.offset.x);  &#125;  @HostListener('mousedown', ['$event'])  onMouseDown(event: MouseEvent) &#123;    this.mouseStart = &#123; x: event.pageX, y: event.pageY &#125;;    const mouseup$ = fromEvent(document, 'mouseup');    this._subscription = mouseup$.subscribe(() =&gt; this.onMouseup());    const mousemove$ = fromEvent(document, 'mousemove')      .pipe(takeUntil(mouseup$))      .subscribe((e: MouseEvent) =&gt; this.onMouseMove(e));    this._subscription.add(mousemove$);  &#125;  onMouseMove(event: MouseEvent) &#123;    this.mouseDelta = &#123; x: (event.pageX - this.mouseStart.x), y: (event.pageY - this.mouseStart.y) &#125;;    this._updatePosition(this.offset.y + this.mouseDelta.y, this.offset.x + this.mouseDelta.x);  &#125;  onMouseup() &#123;    if (this._subscription) &#123;      this._subscription.unsubscribe();      this._subscription = undefined;    &#125;    if (this.mouseDelta) &#123;      this.offset.x += this.mouseDelta.x;      this.offset.y += this.mouseDelta.y;    &#125;  &#125;  private _updatePosition(top: number, left: number) &#123;    this.matDialogRef.updatePosition(&#123;      top: top + 'px',      left: left + 'px'    &#125;);  &#125;  private _getOffset(): Position &#123;    const box = this.container['_elementRef'].nativeElement.getBoundingClientRect();    return &#123;      x: box.left + pageXOffset,      y: box.top + pageYOffset    &#125;;  &#125;&#125;

