// SocketèŠå¤©å®¤ - å®¢æˆ·ç«¯ clientconst net = require('net');const readline = require('readline');const rl = readline.createInterface(process.stdin, process.stdout);rl.question('è¯·è¾“å…¥èŠå¤©æ˜µç§°ï¼š', (nickname) =&gt; &#123;    nickname = nickname.trim();    if (!nickname) &#123;        throw new Error('æ˜µç§°ä¸èƒ½ä¸ºç©ºï¼');    &#125;    // åˆ›å»ºä¸æœåŠ¡ç«¯çš„è¿æ¥    // è®¾ç½®æ­£ç¡®çš„æœåŠ¡ç«¯çš„ipåœ°å€å’Œç«¯å£    let server = net.connect(&#123;port: 2018, host: '127.0.0.1'&#125;, () =&gt; &#123;        // ç™»å…¥æ“ä½œ        let user = &#123;            protocol: 'signin',            from: nickname        &#125;;        // å¾€æœåŠ¡ç«¯ä¼ é€æ•°æ®        server.write(JSON.stringify(user));        console.log(`ã€ç³»ç»Ÿé€šçŸ¥ã€‘å·²æˆåŠŸåŠ å…¥èŠå¤©å®¤ï¼Œå°½æƒ…ç•…èŠå§~`);        // ç›‘å¬æœåŠ¡ç«¯å‘é€è¿‡æ¥çš„æ•°æ®        server.on('data', (chunk) =&gt; &#123;            try &#123;                let serverDataContent = JSON.parse(chunk.toString().trim());                let protocol = serverDataContent.protocol;                switch (protocol) &#123;                    case 'online':                        console.log(`\nã€ç³»ç»Ÿé€šçŸ¥ã€‘æ¬¢è¿ï¼š$&#123;serverDataContent.online&#125;ï¼ŒåŠ å…¥èŠå¤©å®¤ï¼Œå½“å‰åœ¨çº¿äººæ•°ï¼š$&#123;serverDataContent.onlineCount&#125;\n`);                        rl.prompt();                        break;                    case 'offline':                        console.log(`\nã€ç³»ç»Ÿé€šçŸ¥ã€‘$&#123;serverDataContent.offline&#125;ä¸‹çº¿äº†ï¼Œå½“å‰åœ¨çº¿äººæ•°ï¼š$&#123;serverDataContent.onlineCount&#125;\n`);                        rl.prompt();                        break;                    case 'broadcast':                        console.log(`\n[@æ‰€æœ‰äºº] $&#123;serverDataContent.from&#125;&gt; $&#123;serverDataContent.message&#125;\n`);                        rl.prompt();                        break;                    case 'p2p':                        console.log(`\n[@$&#123;serverDataContent.to&#125;] $&#123;serverDataContent.from